import{useEffect,useState}from"react";import Axios from"axios";const useBookmarkData=()=>{const storedUserId=sessionStorage.getItem("userId"),[bookmarkData,setBookmarkData]=useState();return useEffect(()=>{const fetchBookmark=async()=>{try{const response=await Axios.get(`http://localhost:8000/savedCourses/${storedUserId}`);setBookmarkData(response.data)}catch(error){console.error("Error fetching user data:",error)}};fetchBookmark()},[]),bookmarkData};export default useBookmarkData;