import{useEffect,useState}from"react";import Axios from"axios";export default function useCourseData(){const[courseData,setCourseData]=useState();return useEffect(()=>{const fetchCourse=async()=>{try{const response=await Axios.get("http://localhost:8000/fetchCourse"),courses=response.data;courses.length>0&&setCourseData(courses)}catch(error){console.error("Error fetching user data:",error)}};fetchCourse()},[]),courseData}